import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { useContext } from 'react'
import { Button, Text, View } from 'react-native'
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler';
import { Center } from '../components/Center';
import { styles } from '../CSS/CSS';
import { AuthNavProps } from '../navigation/AuthParamList';
import { AuthContext } from '../navigation/AuthProvider';

export function Login({navigation}: AuthNavProps<'Login'>){
    const {login} = useContext(AuthContext);
    return (
        <View>
            <Text style={styles.title}>APORI</Text>
            <Text style={styles.title2}>Se connecter</Text>
            <TextInput />
            <TextInput />
            <TouchableOpacity>
                <Text>Mot de passe oublié ?</Text>
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => navigation.navigate('Register')}>
                <Text>{'Pas encore de compte ? \nCliquez ici pour en créer un !'}</Text>
            </TouchableOpacity>
            
            <TouchableOpacity
                onPress = {() => login()}>
                    <Text> {'->'} </Text>
            </TouchableOpacity>
        </View>
    )
}
