import React from 'react'
import {StyleSheet} from 'react-native'

const styles = StyleSheet.create({
    body:{
        padding:5
    },
    title: {
        flex:1,
        justifyContent: 'center',
        backgroundColor:'red',
        textAlign:'center',
        textTransform : 'capitalize',
        fontSize:20
    },
    title2: {
        flex:1,
        justifyContent: 'center',
        backgroundColor:'green',
        textAlign:'center',
        fontSize:15,
        padding:5
    },
    inputText:{
        borderColor:'blue',
        borderWidth:1,
        padding:5
    },
    hint:{
        color:'blue',
        fontSize:8,
       },
    hint2:{
        color:'blue',
        textAlign:'center',
        textDecorationLine:'underline'
    },
    modal:{
        borderColor:'blue',
        borderWidth:1,
        padding:5,
        zIndex:9999,
        position:'absolute',
        backgroundColor:'white',
        margin:'auto'
    },
    informationText:{
        textAlign:'center'
    },
    modalBackground:{
        width:'100%',
        height:'100%',
        backgroundColor:'pink'
    }
})
const buttons = StyleSheet.create({
    connect:{
        backgroundColor: 'blue',
    },
    connectText:{
        color:'white',
        textAlign:'center'
    }
})
export {styles, buttons}
