import AsyncStorage from '@react-native-async-storage/async-storage';
import React, { useContext, useState } from 'react'
import { Button, Text, View } from 'react-native'
import { TextInput, TouchableOpacity } from 'react-native';
import { Center } from '../components/Center';
import { buttons, styles } from '../CSS/CSS';
import { AuthNavProps } from '../navigation/AuthParamList';
import { AuthContext } from '../navigation/AuthProvider';

export function Login({navigation}: AuthNavProps<'Login'>){
    const {login} = useContext(AuthContext);
    const [ModalVisible, setModalVisible] = useState(false);
    return (
        <View>
        {ModalVisible ? 
        <View>
            <View>
            </View>
            <View
                style={styles.modal}>
                <Text style={styles.informationText}>{'Renseignez votre adresse mail ci dessous.\nVous recevrez un email contenant votre nouveau mot de passe que vous pourrez modifier par la suite dans votre profil.'}</Text>
            </View>
        </View>
        :
        null
        }
        <View style={styles.body}>
            <Text style={styles.title}>APORI</Text>
            <Text style={styles.title2}>Se connecter</Text>
            <TextInput 
            autoCompleteType = {'email'}
            keyboardType = {'email-address'}
            placeholder = {'Adresse mail'}
            style={styles.inputText} />
            <TextInput 
            autoCompleteType = {'email'}
            placeholder = {'Mot de passe'}
            secureTextEntry = {true}
            style={styles.inputText}/>
            <TouchableOpacity
                onPress={()=>setModalVisible(true)}>
                <Text style={styles.hint}>Mot de passe oublié ?</Text>
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => navigation.navigate('Register')}>
                <Text style={styles.hint2}>{'Pas encore de compte ? \nCliquez ici pour en créer un !'}</Text>
            </TouchableOpacity>
            
            <TouchableOpacity
                style = {buttons.connect}
                onPress = {() => login()}>
                    <Text style = {buttons.connectText}> {'->'} </Text>
            </TouchableOpacity>
        </View>
    </View>
    )
}
