import React from 'react'
import {StyleSheet} from 'react-native'

const styles = StyleSheet.create({
    body:{
        padding:5
    },
    title: {
        flex:1,
        justifyContent: 'center',
        backgroundColor:'red',
        textAlign:'center',
        textTransform : 'capitalize',
        fontSize:20
    },
    title2: {
        flex:1,
        justifyContent: 'center',
        backgroundColor:'green',
        textAlign:'center',
        fontSize:15
    },
    inputText:{
        borderColor:'blue',
        borderWidth:1,
        
    }
})

export {styles}
